        l2_mid = (i + _sage_const_p5 ) * l2 / steps
        l2_max = (i + _sage_const_1 ) * l2 / steps

        PC2 = (D2(l2_max) - D2(l2_min)) / PE2
        PE1 = D1(lt - l2_mid)

